member.summary:
context.JoinQuery<User, City>((user, city) => new object[] 
{ 
    JoinType.LeftJoin, user.CityId == city.Id 
})

context.JoinQuery<User, City, Province>((user, city, province) => new object[] 
{ 
    JoinType.LeftJoin, user.CityId == city.Id, 
    JoinType.LeftJoin, city.ProvinceId == province.Id 
})
